declare module "*.module.scss" {
  const content: Record<string, string>;
  export default content;
}

declare module 'react-animations';

declare module '*.jpg';
declare module '*.jpeg';

declare module 'lodash';

declare module '*.svg' {
  const content: any;
  export default content;
}

declare module '*.png' {
  const content: any;
  export default content;
}

declare module '*.scss' {
  const content: any;
  export default content;
}